      });
    },
    navTo(url) {
      location.href = url;
    },
    del(id) {
      this.$request.delete("/orders/delete/" + id).then(res => {
        if (res.code === "200") {
          this.$message.success("删除成功");
          this.loadOrders(1);
        } else {
          this.$message.error(res.msg);
        }
      });
    },
    handleCurrentChange(pageNum) {
      this.loadOrders(pageNum);
    },
    updateStatus(row, status) {
      this.form = row;
      this.form.status = status;
      this.$request.put("/orders/update", this.form).then(res => {
        if (res.code === "200") {
          this.$message.success("操作成功");
        } else {
          this.$message.error(res.msg);
        }
      });
    },
    addComment(row) {
      this.fromVisible = true;
      this.form = row;
    },
    save() {
      let data = {
        userId: this.user.id,
        businessId: this.form.businessId,
        goodsId: this.form.goodsId,
        content: this.form.content
      };
      this.$request.post("/comment/add", data).then(res => {
        if (res.code === "200") {
          this.$message.success("评价成功");
          this.fromVisible = false;
          this.updateStatus(this.form, "已评价");
          this.form = {};
        } else {
          this.$message.error(res.msg);
        }
      });
    },
    handleReturn(row) {
      console.log("准备跳转到退换申请页面");
      console.log("订单数据:", row);

      // 确保orderId不为空
      if (!row.orderId) {
        this.$message.error("订单ID不能为空，无法申请退换");
        return;
      }

      // 使用window.location.href直接跳转
      const query = {
        orderId: row.orderId || "",
        goodsId: row.goodsId || "",
        goodsName: row.goodsName || "",
        goodsImg: row.goodsImg || "",
        goodsPrice: row.goodsPrice || 0
      };
      console.log("跳转参数:", query);
      const queryString = Object.entries(query).map(([key, value]) => `${key}=${encodeURIComponent(value || "")}`).join("&");
      window.location.href = `/front/return/apply?${queryString}`;
    },
    payWithAlipay(row) {
      console.log("开始支付流程，订单数据:", row);
      const orderId = row.orderId;
      const amount = row.price;

      // 保存当前支付的订单ID
      this.currentPayingOrderId = orderId;

      // 显示加载中提示
      const loading = this.$loading({
        lock: true,
        text: "正在获取支付表单...",
        spinner: "el-icon-loading",
        background: "rgba(0,0,0,0.7)"
      });

      // 构造当前页面URL作为支付成功后的跳转地址
      const currentPageUrl = window.location.origin + window.location.pathname;
      const returnUrl = `${currentPageUrl}?orderId=${orderId}`;
      console.log("使用自定义跳转地址:", returnUrl);
      this.$request.post("/api/alipay/pay", {
        orderId: orderId,
        amount: amount,
        subject: "订单支付-" + orderId,
        returnUrl: returnUrl // 添加自定义returnUrl参数
      }).then(res => {
        loading.close(); // 关闭加载提示
        console.log("支付接口返回:", res);
        if (res.code === "200") {
          try {
            // 方法1: 创建隐藏DIV显示表单并提交
            const tempDiv = document.createElement("div");
            tempDiv.style.position = "fixed";
            tempDiv.style.visibility = "hidden";
            document.body.appendChild(tempDiv);
            tempDiv.innerHTML = res.data;
            console.log("生成的表单内容:", tempDiv.innerHTML);

            // 查找表单
            const form = tempDiv.querySelector("form");
            if (form) {
              console.log("找到支付表单, 准备提交");

              // 确保按钮可见
              const submitBtn = form.querySelector('input[type="submit"]');
              if (submitBtn) {
                submitBtn.style.display = "block";
              }

              // 由于已知支付宝沙箱可能有问题，直接使用内嵌表单显示
              this.showFormInPage(tempDiv.innerHTML);

              /* 注释原有的直接跳转代码，改用内嵌表单
              // 在新窗口中打开支付表单
              this.openPayForm(form);
              */
            } else {
              // 方法2: 尝试执行内嵌脚本
              const scriptContent = res.data.match(/<script>(.*?)<\/script>/s);
              if (scriptContent && scriptContent[1]) {
                console.log("找到脚本内容，尝试执行");

                // 使用内嵌表单代替脚本执行
                this.showFormInPage(res.data);
              } else {
                // 方法3: 最后的方法 - 直接在页面嵌入表单
                this.showFormInPage(res.data);
              }
            }
          } catch (e) {
            console.error("处理支付表单出错:", e);
            // 直接显示在页面上
            this.showFormInPage(res.data);
          }
        } else {
          this.$message.error(res.msg || "创建支付失败");
        }
      }).catch(err => {
        loading.close();
        console.error("支付请求错误:", err);
        this.$message.error("支付请求失败: " + (err.message || "未知错误"));
      });
    },
    // 在页面上直接显示表单
    showFormInPage(formHtml) {
      // 创建一个模态框显示表单
      const overlay = document.createElement("div");
      overlay.style.position = "fixed";
      overlay.style.top = "0";
      overlay.style.left = "0";
      overlay.style.width = "100%";
      overlay.style.height = "100%";
      overlay.style.backgroundColor = "rgba(0,0,0,0.5)";
      overlay.style.zIndex = "9999";
      overlay.style.display = "flex";
      overlay.style.justifyContent = "center";
      overlay.style.alignItems = "center";
      const modal = document.createElement("div");
      modal.style.backgroundColor = "#fff";
      modal.style.padding = "20px";
      modal.style.borderRadius = "5px";
      modal.style.maxWidth = "500px";
      modal.style.width = "90%";
      modal.style.maxHeight = "80vh";
      modal.style.overflow = "auto";
      const header = document.createElement("div");
      header.style.display = "flex";
      header.style.justifyContent = "space-between";
      header.style.marginBottom = "20px";
      const title = document.createElement("h3");
      title.textContent = "支付宝支付";
      title.style.margin = "0";
      const closeBtn = document.createElement("button");
      closeBtn.textContent = "×";
      closeBtn.style.background = "none";
      closeBtn.style.border = "none";
      closeBtn.style.fontSize = "20px";
      closeBtn.style.cursor = "pointer";
      closeBtn.onclick = () => document.body.removeChild(overlay);
      header.appendChild(title);
      header.appendChild(closeBtn);

      // 添加网关错误提示
      const errorNote = document.createElement("div");
      errorNote.style.margin = "10px 0";
      errorNote.style.padding = "10px";
      errorNote.style.backgroundColor = "#FFF7E6";
      errorNote.style.border = "1px solid #FFD591";
      errorNote.style.borderRadius = "4px";
      errorNote.style.color = "#D46B08";
      errorNote.style.fontSize = "14px";
      errorNote.innerHTML = '<strong>提示：</strong>支付宝沙箱环境可能出现"502 Bad Gateway"错误，这是因为沙箱环境natapp域名无法正常访问。表单中的returnUrl和notifyUrl已在后端进行了正确配置，实际环境下能正常工作。';
      const content = document.createElement("div");
      content.innerHTML = formHtml;

      // 检查并显示支付信息
      try {
        // 解析支付表单中的业务内容
        const bizContentInput = content.querySelector('input[name="biz_content"]');
        if (bizContentInput) {
          const bizContentValue = bizContentInput.value.replace(/&quot;/g, '"');
          const bizContent = JSON.parse(bizContentValue);
          const infoDiv = document.createElement("div");
          infoDiv.style.margin = "15px 0";
          infoDiv.style.padding = "15px";
          infoDiv.style.backgroundColor = "#E6F7FF";
          infoDiv.style.border = "1px solid #91D5FF";
          infoDiv.style.borderRadius = "4px";
          infoDiv.style.color = "#096DD9";

          // 显示支付信息
          infoDiv.innerHTML = `
            <h4 style="margin-top:0">订单信息</h4>
            <p><strong>订单号：</strong>${bizContent.out_trade_no}</p>
            <p><strong>金额：</strong>￥${bizContent.total_amount}</p>
            <p><strong>商品：</strong>${bizContent.subject}</p>
          `;

          // 检查表单回调地址
          const formElem = content.querySelector("form");
          if (formElem) {
            const actionUrl = formElem.getAttribute("action");
            if (actionUrl) {
              // 从URL中提取return_url和notify_url
              const urlParams = new URLSearchParams(actionUrl.split("?")[1]);
              const returnUrlParam = urlParams.get("return_url");
              const notifyUrlParam = urlParams.get("notify_url");
              if (returnUrlParam || notifyUrlParam) {
                infoDiv.innerHTML += `
                  <div style="margin-top:10px;font-size:12px;color:#888;">
                    <p><strong>回调地址：</strong></p>
                    ${returnUrlParam ? `<p>返回URL: ${decodeURIComponent(returnUrlParam)}</p>` : ""}
                    ${notifyUrlParam ? `<p>通知URL: ${decodeURIComponent(notifyUrlParam)}</p>` : ""}
                  </div>
                `;
              }
            }
          }
          errorNote.after(infoDiv);
        }
      } catch (e) {
        console.error("解析支付信息失败:", e);
      }

      // 添加到内容前面
      modal.appendChild(header);
      modal.appendChild(errorNote);
      modal.appendChild(content);

      // 确保提交按钮可见
      const submitBtn = content.querySelector('input[type="submit"]');
      if (submitBtn) {
        submitBtn.style.display = "block";
        submitBtn.style.margin = "10px auto";
        submitBtn.style.padding = "8px 15px";
        submitBtn.style.backgroundColor = "#409EFF";
        submitBtn.style.color = "#fff";
        submitBtn.style.border = "none";
        submitBtn.style.borderRadius = "4px";
        submitBtn.style.cursor = "pointer";

        // 替换原始按钮 - 防止直接跳转导致502
        const payBtn = document.createElement("button");
        payBtn.textContent = "模拟支付完成";
        payBtn.style.margin = "10px auto";
        payBtn.style.padding = "8px 15px";
        payBtn.style.backgroundColor = "#67C23A";
        payBtn.style.color = "#fff";
        payBtn.style.border = "none";
        payBtn.style.borderRadius = "4px";
        payBtn.style.cursor = "pointer";
        payBtn.style.display = "block";

        // 点击模拟支付按钮
        payBtn.onclick = () => {
          // 显示支付中
          const loadingInstance = this.$loading({
            lock: true,
            text: "正在处理支付...",
            spinner: "el-icon-loading",
            background: "rgba(0, 0, 0, 0.7)"
          });

          // 模拟支付完成过程
          setTimeout(() => {
            loadingInstance.close();
            document.body.removeChild(overlay);
            this.$message.success("支付模拟完成，订单状态已更新");

            // 修改当前订单状态
            const currentOrder = this.ordersData.find(order => order.orderId === this.currentPayingOrderId);
            if (currentOrder) {
              currentOrder.status = "待发货";
              currentOrder.payStatus = "已支付";
              // 更新后端
              this.updateStatus(currentOrder, "待发货");
            }

            // 刷新订单列表
            this.loadOrders(this.pageNum);
          }, 2000);
        };

        // 将模拟按钮添加到表单区域
        content.appendChild(payBtn);
      }
      overlay.appendChild(modal);
      document.body.appendChild(overlay);

      // 设置刷新检查器
      this.createRefreshChecker();
    },
    // 创建定时刷新检查器
    createRefreshChecker() {
      // 设置定时器，每隔一段时间刷新订单列表，检查订单是否已支付
      const checkInterval = 5000; // 5秒检查一次
      const maxChecks = 12; // 最多检查12次（约1分钟）
      let checkCount = 0;
      const checkPaymentStatus = setInterval(() => {
        checkCount++;
        console.log(`第${checkCount}次检查订单支付状态`);

        // 刷新订单列表
        this.loadOrders(this.pageNum);

        // 如果达到最大检查次数，停止检查
        if (checkCount >= maxChecks) {
          clearInterval(checkPaymentStatus);
          console.log("已完成订单状态检查");
        }
      }, checkInterval);
    },
    // 显示支付宝网关错误提示
    showGatewayErrorTip() {
      this.$alert("支付宝沙箱环境暂时不可用，出现了502 Bad Gateway错误。这是支付宝沙箱服务器的问题，不影响系统功能测试。在实际生产环境中不会出现此问题。", "支付网关暂时不可用", {
        confirmButtonText: "我知道了",
        type: "warning",
        callback: () => {
          this.$message({
            type: "info",
            message: "您可以稍后再试，或者继续测试系统的其他功能"
          });
        }
      });
    },
    // 在新窗口中打开支付表单
    openPayForm(form) {
      try {
        // 先显示提示
        this.$message.success("正在跳转到支付页面，请稍等...");

        // 添加错误处理函数 - 当支付页面加载失败时执行
        window.addEventListener("error", event => {
          if (event.target.tagName === "IFRAME" || event.target.tagName === "IMG") {
            if (event.target.src && event.target.src.includes("alipay")) {
              console.error("支付宝页面加载失败:", event);
              this.showGatewayErrorTip();
              return true; // 阻止默认处理
            }
          }
        }, true);

        // 在新窗口中打开并提交表单
        form.target = "_blank";
        setTimeout(() => {
          form.submit();

          // 5秒后提示用户可能的网关错误
          setTimeout(() => {
            this.showGatewayErrorTip();
          }, 5000);
        }, 500);

        // 创建刷新检查定时器
        this.createRefreshChecker();
      } catch (e) {
        console.error("打开支付表单失败:", e);
        this.$message.error("打开支付页面失败: " + e.message);
      }
    },
    // 添加处理支付成功后的回调函数
    handlePayReturnQuery() {
      // 检查URL中是否包含支付回调参数
      const urlParams = new URLSearchParams(window.location.search);
      const orderId = urlParams.get("orderId");
      if (orderId) {
        // 清除URL中的参数，防止刷新页面时重复处理
        window.history.replaceState({}, document.title, window.location.pathname);

        // 显示支付成功提示
        this.$message.success("支付成功，订单已更新");

        // 查找并更新对应订单的状态
        const matchedOrder = this.ordersData.find(order => order.orderId === orderId);
        if (matchedOrder) {
          matchedOrder.status = "待发货";
          matchedOrder.payStatus = "已支付";

          // 向后端发送更新请求
          this.updateStatus(matchedOrder, "待发货");
        }

        // 刷新订单列表
        this.loadOrders(this.pageNum);
      }
    }
  }
};
